mapper.update:
UPDATE emp
username = #{username}, password = #{password}, name = #{name}, gender = #{gender}, phone = #{phone}, job = #{job}, salary = #{salary}, image = #{image}, entry_date = #{entryDate}, dept_id = #{deptId}, update_time = #{updateTime}
WHERE id = #{id}

mapper.select:
select emp.*, dept.name deptName from emp left join dept on emp.dept_id = dept.id





        emp.name like concat('%',#{name},'%')



        and emp.gender = #{gender}



        and emp.entry_date between #{begin} and #{end}


order by emp.update_time desc

select
if(gender = 1, '男性員工', '女性員工') name ,
count(*) value
from emp group by gender;
